<template><div class="message-list">    <!--<div class="asName">-->        <!--{{item.name}}-->    <!--</div>-->    <div>        <ul class="asList">            <li v-for="it in item.messageList">                <img :src="item.imgSrc" class="touxiang">                {{it.date +' - '+ it.msg}}            </li>        </ul>    </div>    <div>        <ul v-if="selfMsg" class="selfList">            <li v-for="sit in selfMsg">                {{sit.msg   +' - '+ sit.date}}            </li>        </ul>    </div>    <div class="btn">        <input type="text" v-model="msg">        <button @click="send">发送</button>    </div></div></template><script>export default{    props : ['item','selfMsg'],    data : function(){        return {            msg : ""        }    },    methods:{        send:function(){            this.$emit('dispatchSend',{msg : this.msg})            this.msg = "";        }    }}</script><style scoped>    .touxiang{        height: 38px;        width: 38px;    }    .asName{        text-align: center;    }    .asList>li{        border-bottom: 1px solid #c3c3c3;        height:40px;        line-height: 40px;    }    .btn{        position: absolute;        bottom:0;        left:0;        display: flex;        justify-content: center;        width: 100%;    }    .btn input{        border:1px solid #cccccc;        height:40px;        width:800px;        /*float: left;*/    }    .btn button{        height: 42px;        width: 100%;    }    .selfList>li{        text-align: right;        border-bottom: 1px solid #c3c3c3;        height:40px;        line-height: 40px;    }</style>