<template><div>    <top :ifshow="ifshow">        <span>消息</span>    </top>    <ChatList :chat-list="chatList"></ChatList></div></template><script>    import ChatList from '../components/ChatList.vue'    import top from '../components/top.vue'export default{        data:function () {            return{                ifshow:false            }        },components:{    ChatList,    top},    created : function(){        this.$http.get('/src/static/data.json').then(function(res){            this.$store.commit('setChatList',res.data);        },function(res){            console.log(res.status + ' @' +new Date())        })    },    computed : {        chatList : function(){            return this.$store.getters.getChatList        }    }}</script><style scoped></style>