<template><div>    <top :ifshow="ifshow">        <span>            {{getname}}        </span>    </top>    <MessageList :item="getAsMessage" :selfMsg="msg" @dispatchSend="resSend"></MessageList></div></template><script>    import MessageList from '../components/MessageList.vue'    import top from  '../components/top.vue'export default{    components:{        MessageList,        top    },    computed : {        getId : function(){            var id = this.$route.query.id;            return id        },        getAsMessage : function(){            return this.$store.getters.getAsMessage;        },        getname : function () {            return this.$store.getters.getAsMessage.name        }    },    created:function(){        this.$store.commit('clearAsMessage');        this.$http.get('/src/static/tsconfig.json').then(function(res){            for(var i=0;i<res.data.length;i++){                if(res.data[i].id == this.getId){                    this.$store.commit('setAsMessage',res.data[i])                }            }        },function(res){        })    },    data:function(){      return {          msg : [],          ifshow:true      }    },    methods:{        resSend:function(opt){            var nowDate = new Date().getHours() + ':'+ new Date().getMinutes()            this.msg.push({msg:opt.msg,date:nowDate})        }    }}</script><style scoped></style>